// Javascript
const arr = ["Daffa", "Kurnia", "Fatah"];

function greeting() {
  const name = arr.find((item) => item === "Daffa");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, Daffa!

// TypeScript
const arr: string[] = ["Daffa", "Kurnia", "Fatah"];

function greeting(): string {
  const name: string | undefined = arr.find((item) => item === "Daffa");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, Daffa!
