// Javascript
const arr = ["taufiqurrahman"];

function greeting() {
  const name = arr.find((item) => item === "taufiqurrahman");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, taufiqurrahman!

// TypeScript
const arr: string[] = ["taufiqurrahman"];

function greeting(): string {
  const name: string | undefined = arr.find((item) => item === "taufiqurrahman");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, taufiqurrahman!
